type column >/dev/null 2>&1 || { echo >&2 "ERROR: You do not have 'bsdmainutils' installed."; exit 1; }

#
# Process command line
#
# Pass all arguments before 'operation' to cURL except the ones we override
#
take_user=false
take_host=false
take_accept_header=false
take_contenttype_header=false

for key in "$@"; do
# Take the value of -u|--user argument
if [[ "$take_user" = true ]]; then
    basic_auth_credential="$key"
    take_user=false
    continue
fi
# Take the value of --host argument
if [[ "$take_host" = true ]]; then
    host="$key"
    take_host=false
    continue
fi
# Take the value of --accept argument
if [[ "$take_accept_header" = true ]]; then
    header_accept=$(lookup_mime_type "$key")
    take_accept_header=false
    continue
fi
# Take the value of --content-type argument
if [[ "$take_contenttype_header" = true ]]; then
    header_content_type=$(lookup_mime_type "$key")
    take_contenttype_header=false
    continue
fi
case $key in
    -h|--help)
    if [[ "x$operation" == "x" ]]; then
        print_help
        exit 0
    else
        eval "print_${operation}_help"
        exit 0
    fi
    ;;
    -V|--version)
    print_version
    exit 0
    ;;
    --about)
    print_about
    exit 0
    ;;
    -u|--user)
    take_user=true
    ;;
    --host)
    take_host=true
    ;;
    --force)
    force=true
    ;;
    -ac|--accept)
    take_accept_header=true
    ;;
    -ct|--content-type)
    take_contenttype_header=true
    ;;
    --dry-run)
    print_curl=true
    ;;
    -nc|--no-colors)
        RED=""
        GREEN=""
        YELLOW=""
        BLUE=""
        MAGENTA=""
        CYAN=""
        WHITE=""
        BOLD=""
        OFF=""
        result_color_table=( "" "" "" "" "" "" "" )
    ;;
    logoutSelfUsingGET)
    operation="logoutSelfUsingGET"
    ;;
    logoutSelfUsingGET1)
    operation="logoutSelfUsingGET1"
    ;;
    addSelfUsingPOST)
    operation="addSelfUsingPOST"
    ;;
    addNewPlantUsingPOST)
    operation="addNewPlantUsingPOST"
    ;;
    deletePlantByIdUsingDELETE)
    operation="deletePlantByIdUsingDELETE"
    ;;
    findPlantByNameLikeUsingGET)
    operation="findPlantByNameLikeUsingGET"
    ;;
    getPlantByIdUsingGET)
    operation="getPlantByIdUsingGET"
    ;;
    getPlantByNameUsingGET)
    operation="getPlantByNameUsingGET"
    ;;
    getPlantBySpeciesLikeUsingGET)
    operation="getPlantBySpeciesLikeUsingGET"
    ;;
    getPlantBySpeciesUsingGET)
    operation="getPlantBySpeciesUsingGET"
    ;;
    listAllPlantsUsingGET)
    operation="listAllPlantsUsingGET"
    ;;
    updateFullUserUsingPUT)
    operation="updateFullUserUsingPUT"
    ;;
    addNewRoleUsingPOST)
    operation="addNewRoleUsingPOST"
    ;;
    getRoleByIdUsingGET)
    operation="getRoleByIdUsingGET"
    ;;
    getRoleByNameUsingGET)
    operation="getRoleByNameUsingGET"
    ;;
    listRolesUsingGET)
    operation="listRolesUsingGET"
    ;;
    putUpdateRoleUsingPUT)
    operation="putUpdateRoleUsingPUT"
    ;;
    addNewUserUsingPOST)
    operation="addNewUserUsingPOST"
    ;;
    deleteUserByIdUsingDELETE)
    operation="deleteUserByIdUsingDELETE"
    ;;
    getCurrentUserInfoUsingGET)
    operation="getCurrentUserInfoUsingGET"
    ;;
    getUserByIdUsingGET)
    operation="getUserByIdUsingGET"
    ;;
    getUserByNameUsingGET)
    operation="getUserByNameUsingGET"
    ;;
    getUserLikeNameUsingGET)
    operation="getUserLikeNameUsingGET"
    ;;
    listAllUsersUsingGET)
    operation="listAllUsersUsingGET"
    ;;
    updateFullUserUsingPUT1)
    operation="updateFullUserUsingPUT1"
    ;;
    updateUserUsingPATCH)
    operation="updateUserUsingPATCH"
    ;;
    addNewUserEmailUsingPOST)
    operation="addNewUserEmailUsingPOST"
    ;;
    deleteUserEmailByIdUsingDELETE)
    operation="deleteUserEmailByIdUsingDELETE"
    ;;
    getUserEmailByIdUsingGET)
    operation="getUserEmailByIdUsingGET"
    ;;
    listAllUseremailsUsingGET)
    operation="listAllUseremailsUsingGET"
    ;;
    updateUserEmailUsingPUT)
    operation="updateUserEmailUsingPUT"
    ;;
    *==*)
    # Parse body arguments and convert them into top level
    # JSON properties passed in the body content as strings
    if [[ "$operation" ]]; then
        IFS='==' read -r body_key sep body_value <<< "$key"
        body_parameters[${body_key}]="\"${body_value}\""
    fi
    ;;
    *:=*)
    # Parse body arguments and convert them into top level
    # JSON properties passed in the body content without qoutes
    if [[ "$operation" ]]; then
        # ignore error about 'sep' being unused
        # shellcheck disable=SC2034
        IFS=':=' read -r body_key sep body_value <<< "$key"
        body_parameters[${body_key}]=${body_value}
    fi
    ;;
    +\([^=]\):*)
    # Parse header arguments and convert them into curl
    # only after the operation argument
    if [[ "$operation" ]]; then
        IFS=':' read -r header_name header_value <<< "$key"
        header_arguments[$header_name]=$header_value
    else
        curl_arguments+=" $key"
    fi
    ;;
    -)
    body_content_temp_file=$(mktemp)
    cat - > "$body_content_temp_file"
    ;;
    *=*)
    # Parse operation arguments and convert them into curl
    # only after the operation argument
    if [[ "$operation" ]]; then
        IFS='=' read -r parameter_name parameter_value <<< "$key"
        if [[ -z "${operation_parameters[$parameter_name]+foo}" ]]; then
            operation_parameters[$parameter_name]=$(url_escape "${parameter_value}")
        else
            operation_parameters[$parameter_name]+=":::"$(url_escape "${parameter_value}")
        fi
    else
        curl_arguments+=" $key"
    fi
    ;;
    *)
    # If we are before the operation, treat the arguments as cURL arguments
    if [[ "x$operation" == "x" ]]; then
        # Maintain quotes around cURL arguments if necessary
        space_regexp="[[:space:]]"
        if [[ $key =~ $space_regexp ]]; then
            curl_arguments+=" \"$key\""
        else
            curl_arguments+=" $key"
        fi
    fi
    ;;
esac
done


# Check if user provided host name
if [[ -z "$host" ]]; then
    ERROR_MSG="ERROR: No hostname provided!!! You have to  provide on command line option '--host ...'"
    exit 1
fi

# Check if user specified operation ID
if [[ -z "$operation" ]]; then
    ERROR_MSG="ERROR: No operation specified!!!"
    exit 1
fi


# Run cURL command based on the operation ID
case $operation in
    logoutSelfUsingGET)
    call_logoutSelfUsingGET
    ;;
    logoutSelfUsingGET1)
    call_logoutSelfUsingGET1
    ;;
    addSelfUsingPOST)
    call_addSelfUsingPOST
    ;;
    addNewPlantUsingPOST)
    call_addNewPlantUsingPOST
    ;;
    deletePlantByIdUsingDELETE)
    call_deletePlantByIdUsingDELETE
    ;;
    findPlantByNameLikeUsingGET)
    call_findPlantByNameLikeUsingGET
    ;;
    getPlantByIdUsingGET)
    call_getPlantByIdUsingGET
    ;;
    getPlantByNameUsingGET)
    call_getPlantByNameUsingGET
    ;;
    getPlantBySpeciesLikeUsingGET)
    call_getPlantBySpeciesLikeUsingGET
    ;;
    getPlantBySpeciesUsingGET)
    call_getPlantBySpeciesUsingGET
    ;;
    listAllPlantsUsingGET)
    call_listAllPlantsUsingGET
    ;;
    updateFullUserUsingPUT)
    call_updateFullUserUsingPUT
    ;;
    addNewRoleUsingPOST)
    call_addNewRoleUsingPOST
    ;;
    getRoleByIdUsingGET)
    call_getRoleByIdUsingGET
    ;;
    getRoleByNameUsingGET)
    call_getRoleByNameUsingGET
    ;;
    listRolesUsingGET)
    call_listRolesUsingGET
    ;;
    putUpdateRoleUsingPUT)
    call_putUpdateRoleUsingPUT
    ;;
    addNewUserUsingPOST)
    call_addNewUserUsingPOST
    ;;
    deleteUserByIdUsingDELETE)
    call_deleteUserByIdUsingDELETE
    ;;
    getCurrentUserInfoUsingGET)
    call_getCurrentUserInfoUsingGET
    ;;
    getUserByIdUsingGET)
    call_getUserByIdUsingGET
    ;;
    getUserByNameUsingGET)
    call_getUserByNameUsingGET
    ;;
    getUserLikeNameUsingGET)
    call_getUserLikeNameUsingGET
    ;;
    listAllUsersUsingGET)
    call_listAllUsersUsingGET
    ;;
    updateFullUserUsingPUT1)
    call_updateFullUserUsingPUT1
    ;;
    updateUserUsingPATCH)
    call_updateUserUsingPATCH
    ;;
    addNewUserEmailUsingPOST)
    call_addNewUserEmailUsingPOST
    ;;
    deleteUserEmailByIdUsingDELETE)
    call_deleteUserEmailByIdUsingDELETE
    ;;
    getUserEmailByIdUsingGET)
    call_getUserEmailByIdUsingGET
    ;;
    listAllUseremailsUsingGET)
    call_listAllUseremailsUsingGET
    ;;
    updateUserEmailUsingPUT)
    call_updateUserEmailUsingPUT
    ;;
    *)
    ERROR_MSG="ERROR: Unknown operation: $operation"
    exit 1
esac
